#include <Arduino.h>

const int pirPin = 2; // Data pin

void setup() {
  pinMode(pirPin, INPUT);
  Serial.begin(9600);
}

void loop() {
  int state = digitalRead(pirPin);
  if (state == HIGH) {
    Serial.println("Motion detected");
  } else {
    Serial.println("No motion");
  }
  delay(500); // Poll every 0.5 s
}
