#include <DHT.h>
DHT dht(2, DHT22);
void setup() { Serial.begin(9600); dht.begin(); }
void loop() { float h = dht.readHumidity(); float t = dht.readTemperature(); if(isnan(h)||isnan(t)) { Serial.println("Failed to read from DHT22"); } else { Serial.print("Humidity: "); Serial.print(h); Serial.print(" %"); Serial.print("  Temperature: "); Serial.print(t); Serial.println(" *C"); } delay(2000); }
