#include <Arduino.h>

const int lightPin = 2;

void setup() {
  pinMode(lightPin, INPUT);
  Serial.begin(9600);
}

void loop() {
  int lightState = digitalRead(lightPin);
  if (lightState == LOW) {
    Serial.println("Light detected");
  } else {
    Serial.println("No light");
  }
  delay(500);
}
