#include <Arduino.h>
int val11;
int val2;
void setup() {
  pinMode(LED1, OUTPUT);
  Serial.begin(9600);
  Serial.println("Emartee.Com");
  Serial.println("Voltage: ");
  Serial.print("V");
}
void loop() {
  float temp;
  val11 = analogRead(0);
  temp = val11 / 4.092;
  val11 = (int)temp;
  val2 = ((val11 % 100) / 10);
  Serial.println(val2);
  delay(1000);
}

